mapper.update:
update interface_test_param set
id = #{id}, urlId = #{urlId}, name = #{name}, type = #{type}, required = #{required}, defaultValue = #{defaultValue}, description = #{description}
where id = #{id}

mapper.select:
select id, urlId, name, type, required, defaultValue, description from interface_test_param

    where


            ${obj.field} ${obj.operator} #{obj.value}
            ${obj.join} ${obj.field} ${obj.operator} #{obj.value}





    order by

        ${obj}



limit 0, 1

